$.fn.reverseChildren = function() {
  return this.each(function() {
    var $this = $(this);
    $this.children().each(function() {
      $this.prepend(this);
    });
  });
};
$(".comments-content ol").reverseChildren();
$(".sort-option").on("change", function() {
  $(".comments-content ol").reverseChildren();
})
